import React, { Component } from 'react';
import L from 'leaflet';
import 'leaflet-routing-machine';
import 'leaflet-control-geocoder';
import 'leaflet.locatecontrol';
import 'leaflet/dist/leaflet.css';
import 'leaflet-routing-machine/dist/leaflet-routing-machine.css';
import 'leaflet-control-geocoder/dist/Control.Geocoder.css';
import 'leaflet.locatecontrol/dist/L.Control.Locate.min.css';

class MapComponent extends Component {
  componentDidMount() {
    // Leaflet map initialization code
    const map = L.map('map').setView([25.5941, 85.1376], 11);
    const mapLink = "<a href='http://openstreetmap.org'>OpenStreetMap</a>";
    L.tileLayer('http://{s}.tile.osm.org/{z}/{x}/{y}.png', {
      attribution: 'Leaflet &copy; ' + mapLink + ', contribution',
      maxZoom: 18,
    }).addTo(map);

    const busIcon = L.icon({
      iconUrl: 'bus.png',
      iconSize: [40, 40],
    });

    const marker = L.marker([25.5941, 85.1376], { icon: busIcon }).addTo(map);

    // Click event handler
    map.on('click', (e) => {
      const newMarker = L.marker([e.latlng.lat, e.latlng.lng]).addTo(map);
      L.Routing.control({
        waypoints: [
          L.latLng(25.5941, 85.1376),
          L.latLng(e.latlng.lat, e.latlng.lng),
        ],
      }).on('routesfound', (e) => {
        const routes = e.routes;
        e.routes[0].coordinates.forEach((coord, index) => {
          setTimeout(() => {
            marker.setLatLng([coord.lat, coord.lng]);
          }, 100 * index);
        });
      }).addTo(map);
    });

    // Geolocation
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const pos = {
            lat: position.coords.latitude,
            lng: position.coords.longitude,
          };

          const geolocationMarker = L.marker(pos);
          geolocationMarker.addTo(map);
          map.setView(pos, 11);
        },
        () => {
          console.log('Geolocation is not supported by your browser');
        }
      );
    } else {
      console.log('Geolocation is not supported by your browser');
    }

    L.Control.geocoder().addTo(map);
    L.control.locate().addTo(map);
  }

  render() {
    return (
      <div>
        <div id="map" style={{ width: '100%', height: '100vh' }}></div>
      </div>
    );
  }
}

export default MapComponent;
