import React, { useContext, useEffect, useRef, useState } from "react";
import noteContext from "../context/notes/noteContext.js";
import Noteitem from "./Noteitem.js";
import AddNote from "./AddNote.js";
import { useNavigate } from "react-router-dom";

const Notes = (props) => {
  const context = useContext(noteContext);
  const { notes, getNotes, addNote, editNote, deleteNote } = context;
  let navigate = useNavigate();

  const [showForm, setShowForm] = useState(false);
  const [showBusesSection, setShowBusesSection] = useState(true);
  const ref = useRef(null);
  const refClose = useRef(null);
  const [note, setNote] = useState({
    id: "",
    start: "",
    end: "",
    title: "",
    description: "",
    tag: "",
  });

  useEffect(() => {
    if (localStorage.getItem("token")) {
      getNotes();
    } else {
      navigate("/login");
    }
  }, []);

  const updateNote = (currentNote) => {
    ref.current.click();
    const { _id, start, end, busName, date } = currentNote;
    setNote({
      id: _id,
      start: start || "",
      end: end || "",
      title: busName || "",
      description: date,
      tag: "", // Update this field according to your data model
    });
  };

  const handleClick = (e) => {
    editNote(
      note.id,
      note.start,
      note.end,
      note.title,
      note.description,
      note.tag
    );
    refClose.current.click();
  };

  const onChange = (e) => {
    setNote({ ...note, [e.target.name]: e.target.value });
  };

  const toggleForm = () => {
    setShowForm(!showForm);
  };

  const toggleBusesSection = () => {
    setShowBusesSection(!showBusesSection);
  };

  return (
    <>
      <div
        className="btn-group my-2"
        role="group"
        aria-label="Button group with nested dropdown"
      >
        <div
          className="btn-group"
          role="group"
          aria-label="Basic mixed styles example"
        >
          <button
            type="button"
            className="btn btn-success mx-1 my-1"
            onClick={toggleForm}
          >
            {showForm ? "Hide" : "Add Bus"}
          </button>
          <button
            type="button"
            className="btn btn-primary mx-1 my-1"
            onClick={toggleBusesSection}
          >
            {showBusesSection ? "Hide Buses" : "Show Buses"}
          </button>
        </div>
      </div>
      {showForm && <AddNote />}

      <button
        ref={ref}
        type="button"
        className="btn btn-primary d-none"
        data-bs-toggle="modal"
        data-bs-target="#exampleModal"
      >
        Launch demo modal
      </button>
      <div
        className="modal fade"
        id="exampleModal"
        tabIndex="-1"
        aria-labelledby="exampleModalLabel"
        aria-hidden="true"
      >
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="exampleModalLabel">
                Edit Bus
              </h5>
              <button
                type="button"
                className="btn-close"
                data-bs-dismiss="modal"
                aria-label="Close"
              ></button>
            </div>
            <div className="modal-body">
              <form className="my-3">
                <div className="mb-3">
                  <label htmlFor="start" className="form-label">
                    Starting Position
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="start"
                    name="start"
                    value={note.start}
                    aria-describedby="emailHelp"
                    onChange={onChange}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="end" className="form-label">
                    Destination
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="end"
                    name="end"
                    value={note.end}
                    aria-describedby="emailHelp"
                    onChange={onChange}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="title" className="form-label">
                    Bus Name
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="title"
                    name="title"
                    value={note.title}
                    aria-describedby="emailHelp"
                    onChange={onChange}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="description" className="form-label">
                    Bus Number
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="description"
                    name="description"
                    value={note.description}
                    onChange={onChange}
                    minLength={5}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="tag" className="form-label">
                    Choose an option:
                  </label>
                  <select
                    className="form-select"
                    id="tag"
                    name="tag"
                    value={note.tag}
                    onChange={onChange}
                  >
                    <option value="CNG">CNG</option>
                    <option value="ELECTRIC">ELECTRIC</option>
                  </select>
                </div>
              </form>
            </div>
            <div className="modal-footer">
              <button
                ref={refClose}
                type="button"
                className="btn btn-secondary"
                data-bs-dismiss="modal"
              >
                Close
              </button>
              <button
                disabled={note.title.length < 5 || note.description.length < 5}
                onClick={handleClick}
                type="button"
                className="btn btn-primary"
              >
                Update Bus Details
              </button>
            </div>
          </div>
        </div>
      </div>
      {showBusesSection && (
        <div className="row my-3">
          <h2>Your Buses</h2>
          <div className="container mx-2">
            {notes.length === 0 && "No buses to display"}
          </div>
          {notes.map((note) => {
            return (
              <Noteitem key={note._id} updateNote={updateNote} note={note} />
            );
          })}
        </div>
      )}
    </>
  );
};

export default Notes;
