import React, { useEffect } from 'react';
import L from 'leaflet';
import 'leaflet-routing-machine';
import 'leaflet-control-geocoder';
import 'leaflet.locatecontrol';
import 'leaflet/dist/leaflet.css';
import 'leaflet-routing-machine/dist/leaflet-routing-machine.css';
import 'leaflet-control-geocoder/dist/Control.Geocoder.css';
import 'leaflet.locatecontrol/dist/L.Control.Locate.min.css';

const MapComponent = () => {
  useEffect(() => {
    // Check if the 'map' element exists
    const mapContainer = document.getElementById('map');
    if (!mapContainer) {
      return;
    }

    // Initialize the map
    document.addEventListener("DOMContentLoaded", function () {
      // Your map initialization code here
      const map = L.map(mapContainer).setView([25.5941, 85.1376], 11);

      // Add the OpenStreetMap tile layer
      L.tileLayer('http://{s}.tile.osm.org/{z}/{x}/{y}.png', {
        attribution: 'Leaflet &copy; <a href="http://openstreetmap.org">OpenStreetMap</a> contributors',
        maxZoom: 18,
      }).addTo(map);

      // Create a marker icon
      const busIcon = L.icon({
        iconUrl: 'bus.png',
        iconSize: [40, 40],
      });

      let marker;

      // Add a marker to the map
      marker = L.marker([25.5941, 85.1376], { icon: busIcon }).addTo(map);

      // Handle map click event
      map.on('click', (e) => {
        // Create a routing control with waypoints
        L.Routing.control({
          waypoints: [
            L.latLng(25.5941, 85.1376),
            L.latLng(e.latlng.lat, e.latlng.lng),
          ],
        }).on('routesfound', (e) => {
          // Handle routes found event
          const routes = e.routes;
          e.routes[0].coordinates.forEach((coord, index) => {
            setTimeout(() => {
              marker.setLatLng([coord.lat, coord.lng]);
            }, 100 * index);
          });
        }).addTo(map);
      });

      // Check for geolocation support and set the user's position on the map
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            const pos = {
              lat: position.coords.latitude,
              lng: position.coords.longitude,
            };

            const geolocationMarker = L.marker(pos);
            geolocationMarker.addTo(map);
            map.setView(pos, 11);
          },
          () => {
            console.log('Geolocation is not supported by your browser');
          }
        );
      } else {
        console.log('Geolocation is not supported by your browser');
      }

      // Add geocoder control
      L.Control.geocoder().addTo(map);

      // Add locate control
      L.control.locate().addTo(map);

      // Cleanup function
      return () => {
        if (marker) {
          marker.remove();
        }
        map.remove();
      };
    });
  }, []);

  return (
    <div>
      <div id="map" style={{ width: '100%', height: '500px' }}></div>
    </div>
  );
};

export default MapComponent;
