import React, { useState } from 'react';
import Notes from './Notes.js';
import Compare from "./Compare.js";

const Home = (props) => {
  const { showAlter, notes } = props; // Assuming 'notes' is a prop passed to Home

  const [compareResult, setCompareResult] = useState(null); // Initialize compareResult state

  const handleCompare = (formData) => {
    const { start, end } = formData;
    const matchingNotes = notes.filter(
      (note) => note.estart === start && note.eend === end
    );

    if (matchingNotes.length > 0) {
      setCompareResult(matchingNotes);
    } else {
      setCompareResult("Oops! Details do not match.");
    }
  };

  return (
    <div>
      <Compare note={note} onCompare={handleCompare} />

      {compareResult !== null && (
        <div className="container my-2">
          {Array.isArray(compareResult) ? (
            <div>
              <h3>Matching Bus Details</h3>
              {compareResult.map((match) => (
                <div key={match._id}>
                  <p>Starting Position: {match.start}</p>
                  <p>Destination: {match.end}</p>
                  <p>Bus Name: {match.title}</p>
                  <p>Bus Number: {match.description}</p>
                  <p>Tag: {match.tag}</p>
                </div>
              ))}
            </div>
          ) : (
            <h3>{compareResult}</h3>
          )}
        </div>
      )}
      <div> 
        <Notes showAlter={showAlter} />
      </div>
    </div>
  );
}

export default Home;
