import React, { useState } from 'react';

const SearchForm = ({ items, onSearch }) => {
  const [start, setStart] = useState('');
  const [destination, setDestination] = useState('');
  const [noResults, setNoResults] = useState(false); // State to track if no results are found

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name === 'start') {
      setStart(value);
    } else if (name === 'destination') {
      setDestination(value);
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();

    const filtered = items.filter((item) => {
      if (item && item.estart && item.eend) {
        return (
          item.estart.toLowerCase().includes(start.toLowerCase()) &&
          item.eend.toLowerCase().includes(destination.toLowerCase())
        );
      }
      return false;
    });

    if (filtered.length === 0) {
      // If no results are found, set the state to indicate this
      setNoResults(true);
    } else {
      // If results are found, reset the state
      setNoResults(false);
    }

    // Pass the filtered results back to the parent component
    onSearch(filtered);
  };

  return (
    <div>
      <h2>Search for Items</h2>
      <form onSubmit={handleSearch}>
        <div>
          <label htmlFor="start">Starting Position:</label>
          <input
            type="text"
            id="start"
            name="start"
            value={start}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label htmlFor="destination">Destination:</label>
          <input
            type="text"
            id="destination"
            name="destination"
            value={destination}
            onChange={handleInputChange}
          />
          <button type="submit">Search</button>
        </div>
      </form>
      {noResults && <p>No matching results found.</p>}
    </div>
  );
};

export default SearchForm;
