import React, { useState } from 'react';

const AddItem = (props) => {
    const [item, setItem] = useState({ name: "", description: "" });

    const handleSubmit = async (e) => {
        e.preventDefault();
        const response = await fetch("http://localhost:8000/api/items/add", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem('token')}` // Add authentication token
            },
            body: JSON.stringify(item)
        });
        const json = await response.json();
        console.log(json);
        if (json.success) {
            props.showAlert("Item added successfully", "success");
            // You can add additional logic here, such as clearing the form or updating the item list.
        } else {
            props.showAlert("Failed to add item", "danger");
        }
    }

    const onChange = (e) => {
        setItem({ ...item, [e.target.name]: e.target.value });
    }

    return (
        <div className="mt-3">
            <h2>Add an Item</h2>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="name" className="form-label">Name</label>
                    <input type="text" className="form-control" value={item.name} onChange={onChange} id="name" name="name" />
                </div>
                <div className="mb-3">
                    <label htmlFor="description" className="form-label">Description</label>
                    <textarea className="form-control" value={item.description} onChange={onChange} name="description" id="description" rows="4"></textarea>
                </div>

                <button type="submit" className="btn btn-primary">Add Item</button>
            </form>
        </div>
    );
}

export default AddItem;
