// Notes.js
import React, { useContext, useEffect, useRef, useState } from 'react';
import noteContext from "../context/notes/noteContext.js";
import Noteitem from './Noteitem.js';
import AddNote from './AddNote.js';
import SearchForm from './SearchForm.js';
import { useNavigate } from 'react-router-dom';

const Notes = (props) => {
  const context = useContext(noteContext);
  const { notes, getNotes, editNote } = context;
  let navigate = useNavigate();

  const [showForm, setShowForm] = useState(false);
  const [filteredNotes, setFilteredNotes] = useState([]);

  useEffect(() => {
    if (localStorage.getItem('token')) {
      getNotes();
    } else {
      navigate('/login');
    }
  }, []);

  const ref = useRef(null);
  const refClose = useRef(null);

  const [note, setNote] = useState({ id: "", estart: "", eend: "", etitle: "", edescription: "", etag: "" });

  const updateNote = (currentNote) => {
    ref.current.click();
    const { _id, estart, eend, etitle, edescription, etag } = currentNote;
    setNote({ id: _id, estart: estart || "", eend: eend || "", etitle: etitle || "", edescription: edescription || "", etag: etag || "" });
  }

  const handleClick = (e) => {
    editNote(note.id, note.estart, note.eend, note.etitle, note.edescription, note.etag);
    refClose.current.click();
  }

  const onChange = (e) => {
    setNote({ ...note, [e.target.name]: e.target.value });
  }

  const handleSearchResult = (filteredItems) => {
    setFilteredNotes(filteredItems);
  };

  const toggleForm = () => {
    setShowForm(!showForm);
  }

  return (
    <>
      <SearchForm items={notes} onSearch={handleSearchResult} />

      <button
        type="button"
        className="btn btn-primary"
        onClick={toggleForm}
      >
        {showForm ? 'Hide' : 'Add Bus'}
      </button>

      {showForm && <AddNote showAlert={props.showAlert} />}

      <button ref={ref} type="button" className="btn btn-primary d-none" data-bs-toggle="modal" data-bs-target="#exampleModal">
        Launch demo modal
      </button>

      <div className="row my-3">
        <h2>Your Buses</h2>
        <div className="container mx-2">
          {filteredNotes.length === 0 && 'No buses to display'}
        </div>
        {filteredNotes.map((note) => {
          return <Noteitem key={note.id} updateNote={updateNote} note={note} />;
        })}
      </div>
    </>
  );
}

export default Notes;
