import React, { useContext, useEffect, useRef, useState } from 'react';
import noteContext from "../context/notes/noteContext.js";
import Noteitem from './Noteitem.js'; // Corrected import statement
import SearchForm from './';
import { useNavigate } from 'react-router-dom';

const Notes = (props) => {
  const context = useContext(noteContext);
  const { notes, getNotes, editNote } = context;
  let navigate = useNavigate();

  // Add a state variable to control form visibility
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    if (localStorage.getItem('token')) {
      getNotes();
    } else {
      navigate('/login');
    }
    // eslint-disable-next-line
  }, []);

  const ref = useRef(null);
  const refClose = useRef(null);

  // Initialize the note state with empty values
  const [note, setNote] = useState({ id: "", start: "", destination: "", title: "", description: "", tag: "" });

  const updateNote = (currentNote) => {
    ref.current.click();

    // Make sure to handle the case where etitle might be missing
    const { _id, StatingPosition, endingPosition, busName, busNumber, tag } = currentNote;
    setNote({ id: _id, start: StatingPosition || "", destination: endingPosition || "", title: busName || "", description: busNumber, tag: tag || "" });
  }

  const handleClick = (e) => {
    editNote(note.id, note.start, note.destination, note.title, note.description, note.tag);
    refClose.current.click();
    // props.showAlert("Update successfully","success")
  }

  const onChange = (e) => {
    setNote({ ...note, [e.target.name]: e.target.value });
  }

  // Function to toggle the form visibility
  const toggleForm = () => {
    setShowForm(!showForm);
  }

  return (
    <>
      {/* Add/Edit Bus button */}
      <button
        type="button"
        className="btn btn-primary"
        onClick={toggleForm}
      >
        {showForm ? 'Hide' : 'Add Bus'}
      </button>

      {/* AddNote component is conditionally rendered */}
      {/* Import AddNote and use it here if needed */}
      {/* {showForm && <AddNote showAlert={props.showAlert} />} */}

      {/* Modal for editing a bus */}
      <button ref={ref} type="button" className="btn btn-primary d-none" data-bs-toggle="modal" data-bs-target="#exampleModal">
        Launch demo modal
      </button>
      <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div className="modal-dialog">
          <div className="modal-content">
            {/* ... (rest of your modal code) */}
          </div>
        </div>
      </div>

      <div className="row my-3">
        <h2>Your Buses</h2>
        <div className="container mx-2">
          {notes.length === 0 && 'No buses to display'}
        </div>
        {notes.map((note) => {
          return <Noteitem key={note._id} updateNote={updateNote} note={note} />; // Corrected component name
        })}
      </div>

      {/* Render the SearchForm component */}
      <SearchForm items={notes} />
    </>
  );
}

export default Notes;
