import React, { useContext } from 'react';
import noteContext from "../context/notes/noteContext.js";

const Noteitem = (props) => {
    const context = useContext(noteContext);
    const { deleteNote } = context;
    const { note, updateNote } = props;

    return (
        <div className="col-md-3">
            <div className="card my-3">
                <div className="card-body">
                    <div className="d-flex align-items-center">
                        <h5 className="card-title">{note.busName}</h5>
                        <i className="far fa-trash-alt mx-2" onClick={() => { deleteNote(note._id); }} ></i>
                        <i className="far fa-edit mx-2" onClick={() => { updateNote(note); }}></i>
                    </div>
                    <p className="card-text">Bus Number: {note.busNumber}</p>
                    <p className="card-text">Starting Point: {note.startingPoint}</p>
                    <p className="card-text">Destination: {note.destination}</p>
                    <p className="card-text">Tag: {note.tag}</p>
                </div>
            </div>
        </div>
    )
}

export default Noteitem;
