import React, { useContext, useEffect, useRef, useState } from 'react';
import noteContext from "../context/notes/noteContext.js";
import Noteitem from './Noteitem.js';
import AddNote from './AddNote.js';
import { useNavigate } from 'react-router-dom';

// Starting position of Notes component
const Notes = (props) => {
    const context = useContext(noteContext);
    const { notes, getNotes, editNote } = context;
    let navigate = useNavigate();

    // Add a state variable to control form visibility
    const [showForm, setShowForm] = useState(false);

    useEffect(() => {
        if (localStorage.getItem('token')) {
            getNotes();
        } else {
            navigate('/login');
        }
        // eslint-disable-next-line
    }, []);

    const ref = useRef(null);
    const refClose = useRef(null);

    // Initialize the note state with empty values
    const [note, setNote] = useState({ id: "",estart:"", etitle: "", edescription: "", etag: "" });

    const updateNote = (currentNote) => {
        ref.current.click();

        // Make sure to handle the case where etitle might be missing
        const { _id, busName, busNumber, tag } = currentNote;
        setNote({ id: _id, etitle: busName || "", edescription: busNumber, etag: tag || "" });
    }

    const handleClick = (e) => {
        editNote(note.id, note.etitle, note.edescription, note.etag);
        refClose.current.click();
        // props.showAlert("Update successfully","success")
    }

    const onChange = (e) => {
        setNote({ ...note, [e.target.name]: e.target.value });
    }

    // Function to toggle the form visibility
    const toggleForm = () => {
        setShowForm(!showForm);
    }

    return (
        <>
            {/* Add/Edit Bus button */}
            <button
                type="button"
                className="btn btn-primary"
                onClick={toggleForm}
            >
                {showForm ? 'Hide' : 'Add Bus'}
            </button>

            {/* AddNote component is conditionally rendered */}
            {showForm && <AddNote showAlert={props.showAlert} />}

            {/* Modal for editing a bus */}
            <button ref={ref} type="button" className="btn btn-primary d-none" data-bs-toggle="modal" data-bs-target="#exampleModal">
                Launch demo modal
            </button>
            <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="exampleModalLabel">Edit Bus</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            <form className="my-3">
                                <div className="mb-3">
                                    <label htmlFor="etitle" className="form-label">Bus Name</label>
                                    <input type="text" className="form-control" id="etitle" name="etitle" value={note.etitle} aria-describedby="emailHelp" onChange={onChange} required />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="edescription" className="form-label">Bus Number</label>
                                    <input type="text" className="form-control" id="edescription" name="edescription" value={note.edescription} onChange={onChange} minLength={5} required />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="etag" className="form-label">cng/electric</label>
                                    <input type="text" className="form-control" id="etag" name="etag" value={note.etag} onChange={onChange} />
                                </div>
                            </form>
                        </div>
                        <div className="modal-footer">
                            <button ref={refClose} type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                            <button disabled={note.etitle.length <= 1 || note.edescription.length <= 1} onClick={handleClick} type="button" className="btn btn-primary">Update Bus Details</button>
                        </div>
                    </div>
                </div>
            </div>

            <div className="row my-3">
                <h2>Your Buses</h2>
                <div className="container mx-2">
                    {notes.length === 0 && 'No buses to display'}
                </div>
                {notes.map((note) => {
                    return <Noteitem key={note._id} updateNote={updateNote} note={note} />;
                })}
            </div>
        </>
    );
}
// Ending position of Notes component

export default Notes;
