import React, { useContext, useEffect, useRef, useState } from 'react';
import noteContext from '../context/notes/noteContext.js';
import Noteitem from './Noteitem.js';
import AddNote from './AddNote.js';
import SearchForm from './SearchForm.js';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar.js'; // Import the Navbar component

const Notes = (props) => {
  const context = useContext(noteContext);
  const { notes, getNotes, editNote } = context;
  let navigate = useNavigate();

  const [showForm, setShowForm] = useState(false);
  const ref = useRef(null);
  const refClose = useRef(null);
  const [note, setNote] = useState({
    id: '',
    estart: '',
    eend: '',
    etitle: '',
    edescription: '',
    etag: '',
  });

  useEffect(() => {
    if (localStorage.getItem('token')) {
      getNotes();
    } else {
      navigate('/login');
    }
    // eslint-disable-next-line
  }, []);

  const updateNote = (currentNote) => {
    ref.current.click();

    const { _id, StatingPosition, endingPosition, busName, busNumber, tag } =
      currentNote;
    setNote({
      id: _id,
      estart: StatingPosition || '',
      eend: endingPosition || '',
      etitle: busName || '',
      edescription: busNumber,
      etag: tag || '',
    });
  };

  const handleClick = (e) => {
    editNote(
      note.id,
      note.estart,
      note.eend,
      note.etitle,
      note.edescription,
      note.etag
    );
    refClose.current.click();
    // props.showAlert("Update successfully", "success")
  };

  const onChange = (e) => {
    setNote({ ...note, [e.target.name]: e.target.value });
  };

  return (
    <>
      <Navbar toggleBuses={toggleForm} /> {/* Pass the toggleForm function to the Navbar */}
      {showForm ? 'Hide Buses' : 'Show Buses'}
      <button
        ref={ref}
        type="button"
        className="btn btn-primary d-none"
        data-bs-toggle="modal"
        data-bs-target="#exampleModal"
      >
        Launch demo modal
      </button>
      <div
        className="modal fade"
        id="exampleModal"
        tabIndex="-1"
        aria-labelledby="exampleModalLabel"
        aria-hidden="true"
      >
        {/* ... Modal code ... */}
      </div>
      {showForm && <AddNote showAlert={props.showAlert} />}
      <div className="row my-3">
        <h2>Your Buses</h2>
        <div className="container mx-2">
          {notes.length === 0 && 'No buses to display'}
        </div>
        {notes.map((note) => {
          return (
            <Noteitem key={note._id} updateNote={updateNote} note={note} />
          );
        })}
      </div>
    </>
  );
};

export default Notes;
