import React, { useState } from 'react';

const SearchForm = ({ items }) => {
  const [searchTerm, setSearchTerm] = useState('');

  const handleInputChange = (e) => {
    setSearchTerm(e.target.value.toLowerCase());
  };

  // Filter the items based on the search term
  const filteredItems = items.filter((item) => {
    // Check if the item is defined and has a 'start' property
    if (item && item.start) {
      // Convert 'start' property to lowercase and check for a match with the search term
      return item.start.toLowerCase().includes(searchTerm);
    }
    return false; // Return false if item is undefined or doesn't have 'start' property
  });

  const handleSearchClick = () => {
    // You can perform any specific search action here if needed
    // For now, let's just log the search term
    console.log(`Searching for: ${searchTerm}`);
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Search by starting position"
        value={searchTerm}
        onChange={handleInputChange}
      />
      <button onClick={handleSearchClick}>Search</button>
      <ul>
        {filteredItems.map((item) => (
          <li key={item.id}>{item.start}</li>
        ))}
      </ul>
    </div>
  );
};

export default SearchForm;
