import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = (props) => {
  const [credentials, setCredentials] = useState({ email: '', password: '' });
  const [resetPasswordEmail, setResetPasswordEmail] = useState('');
  const [resetPasswordMode, setResetPasswordMode] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch('http://localhost:8000/api/auth/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ email: credentials.email, password: credentials.password }),
    });
    const json = await response.json();
    console.log(json);
    if (json.success) {
      localStorage.setItem('token', json.authtoken);
      navigate('/');
      props.showAlert('Logged in successfully', 'success');
    } else {
      props.showAlert('Invalid Credentials', 'danger');
    }
  };

  const handleResetPasswordClick = () => {
    setResetPasswordMode(true);
  };

  const handleResetPasswordSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch('http://localhost:8000/api/resetPassword/request', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ email: resetPasswordEmail }),
    });
    const json = await response.json();
    if (json.success) {
      props.showAlert('Password reset email sent', 'success');
      setResetPasswordMode(false);
    } else {
      props.showAlert('Password reset request failed', 'danger');
    }
  };

  const onChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const onResetEmailChange = (e) => {
    setResetPasswordEmail(e.target.value);
  };

  return (
    <div className="mt-3">
      <h2>Login to continue</h2>
      <form onSubmit={handleSubmit}>
        {/* Existing login form fields */}
        <div className="mb-3">
          <label htmlFor="email" className="form-label">
            Email address
          </label>
          <input
            type="email"
            className="form-control"
            value={credentials.email}
            onChange={onChange}
            id="email"
            name="email"
            aria-describedby="emailHelp"
          />
          <div id="emailHelp" className="form-text">
            We'll never share your email with anyone else.
          </div>
        </div>
        <div className="mb-3">
          <label htmlFor="password" className="form-label">
            Password
          </label>
          <input
            type="password"
            className="form-control"
            value={credentials.password}
            onChange={onChange}
            name="password"
            id="password"
          />
        </div>

        <button type="submit" className="btn btn-primary">
          Submit
        </button>

      </form>


    </div>
  );
};

export default Login;
