import React, { useState } from 'react';

const SearchForm = ({ items }) => {
  const [start, setStart] = useState('');
  const [destination, setDestination] = useState('');
  const [filteredItems, setFilteredItems] = useState([]);
  const [searchMessage, setSearchMessage] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name === 'start') {
      setStart(value);
    } else if (name === 'destination') {
      setDestination(value);
    }
  };

  const handleSearch = (e) => {
    e.preventDefault(); // Prevent the default form submission

    // Filter items based on start and destination
    const filtered = items.filter((item) => {
      // Check if item is defined and has 'start' and 'destination' properties
      if (item && item.start && item.destination) {
        return (
          item.start.toLowerCase().includes(start.toLowerCase()) &&
          item.destination.toLowerCase().includes(destination.toLowerCase())
        );
      }
      return false;
    });

    setFilteredItems(filtered);

    // Set search message based on the filter results
    if (filtered.length === 0) {
      setSearchMessage('No matching items found.');
    } else {
      setSearchMessage('');
    }
  };

  return (
    <div>
      <h2>Search for Items</h2>
      <form onSubmit={handleSearch}>
        <div>
          <label htmlFor="start">Starting Position:</label>
          <input
            type="text"
            id="start"
            name="start"
            value={start}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label htmlFor="destination">Destination:</label>
          <input
            type="text"
            id="destination"
            name="destination"
            value={destination}
            onChange={handleInputChange}
          />
          <button type="submit">Search</button>
        </div>
      </form>
      {searchMessage && <p>{searchMessage}</p>}
      {filteredItems.length > 0 && (
        <div>
          <h3>Matching Items:</h3>
          <ul>
            {filteredItems.map((item) => (
              <li key={item.id}>
                Starting: {item.start}, Destination: {item.destination}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default SearchForm;
