import React, { useContext, useEffect, useState } from 'react';
import noteContext from "../context/notes/noteContext.js";
import Noteitem from './Noteitem.js';
import AddNote from './AddNote.js';
import SearchForm from './SearchForm.js';

const Notes = () => {
  const context = useContext(noteContext);
  const { notes, getNotes, editNote } = context;

  useEffect(() => {
    getNotes();
  }, []);

  const [filteredNotes, setFilteredNotes] = useState([]);

  const handleSearch = (filteredItems) => {
    setFilteredNotes(filteredItems);
  };

  return (
    <div>
      <AddNote />
      <SearchForm items={notes} onSearch={handleSearch} />

      {filteredNotes.length === 0 && <p>No matching items found.</p>}

      {filteredNotes.length > 0 && (
        <div>
          <h3>Matching Items:</h3>
          <ul>
            {filteredNotes.map((item) => (
              <Noteitem key={item._id} note={item} updateNote={editNote} />
            ))}
          </ul>
        </div>
      )}

      <h2>Your Buses</h2>
      <div className="container mx-2">
        {notes.length === 0 && 'No buses to display'}
      </div>
      {notes.map((note) => (
        <Noteitem key={note._id} updateNote={editNote} note={note} />
      ))}
    </div>
  );
};

export default Notes;
