import React, { useContext, useState } from 'react';
import noteContext from "../context/notes/noteContext.js";
import PropTypes from 'prop-types';

const AddNote = (props) => {
    const context = useContext(noteContext);
    const { addNote } = context;

    const [note, setNote] = useState({ startingPoint: "", busName: "", destination: "", busNumber: "", tag: "" });

    const handleClick = (e) => {
        e.preventDefault();
        addNote(note.startingPoint, note.busName, note.destination, note.busNumber, note.tag);
        setNote({ startingPoint: "", busName: "", destination: "", busNumber: "", tag: "" });
        // this.props.showAlert("Added successfully", "success")
    }

    const onChange = (e) => {
        setNote({ ...note, [e.target.name]: e.target.value });
    }

    return (
        <div className="container my-3">
            <h2>Add a Bus</h2>
            <form className="my-3">
                <div className="mb-3">
                    <label htmlFor="startingPoint" className="form-label">Starting Point</label>
                    <input type="text" className="form-control" id="startingPoint" name="startingPoint" value={note.startingPoint} onChange={onChange} required />
                </div>
                <div className="mb-3">
                    <label htmlFor="destination" className="form-label">Destination</label>
                    <input type="text" className="form-control" id="destination" name="destination" value={note.destination} onChange={onChange} required />
                </div>
                <div className="mb-3">
                    <label htmlFor="busName" className="form-label">Bus Name</label>
                    <input type="text" className="form-control" id="busName" name="busName" value={note.busName} onChange={onChange} required />
                </div>
                <div className="mb-3">
                    <label htmlFor="busNumber" className="form-label">Bus Number</label>
                    <input type="number" className="form-control" id="busNumber" name="busNumber" value={note.busNumber} onChange={onChange} required />
                </div>
                <div className="mb-3">
                    <label htmlFor="tag" className="form-label">
                        Choose an option:
                    </label>
                    <select className="form-select" id="tag" name="tag" value={note.tag} onChange={onChange}>
                        <option value="CNG">CNG</option>
                        <option value="ELECTRIC">ELECTRIC</option>
                    </select>
                </div>

                <button disabled={note.startingPoint.length <= 1 || note.busName.length <= 1} type="submit" className="btn btn-primary" onClick={handleClick}>Add Note</button>
            </form>
        </div>
    )
}

export default AddNote;
