import React, { useState } from 'react';

const CompareForm = ({ note }) => {
  const [formData, setFormData] = useState({ start: '', end: '' });

  const { start, end } = formData;

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (start === note.start && end === note.end) {
      // Matched, display details
      return (
        <div>
          <h3>Bus Details</h3>
          <p>Starting Position: {note.start}</p>
          <p>Destination: {note.end}</p>
          <p>Bus Name: {note.title}</p>
          <p>Bus Number: {note.description}</p>
          <p>Tag: {note.tag}</p>
        </div>
      );
    } else {
      // Not matched, show "Oops!"
      return <h3>Oops! Details do not match.</h3>;
    }
  };

  return (
    <div>
      <h2>Compare Bus Details</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="start" className="form-label">
            Starting Position
          </label>
          <input
            type="text"
            className="form-control"
            id="start"
            name="start"
            value={start}
            onChange={handleInputChange}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="end" className="form-label">
            Destination
          </label>
          <input
            type="text"
            className="form-control"
            id="end"
            name="end"
            value={end}
            onChange={handleInputChange}
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Compare
        </button>
      </form>
    </div>
  );
};

export default CompareForm;
