import React, { useContext, useState } from 'react';
import noteContext from '../context/notes/noteContext.js';
import CompareForm from './CompareForm.js';

const Noteitem = (props) => {
  const context = useContext(noteContext);
  const { deleteNote } = context;
  const { note, updateNote } = props;

  const [showCompareForm, setShowCompareForm] = useState(false);

  const handleCompareClick = () => {
    setShowCompareForm(!showCompareForm);
  };

  return (
    <div className="col-md-3">
      <div className="card my-3">
        <div className="card-body">
          <div className="d-flex align-items-center">
            <h5 className="card-title">{note.title}</h5>
            <i className="far fa-trash-alt mx-2" onClick={() => deleteNote(note._id)}></i>
            <i className="far fa-edit mx-2" onClick={() => updateNote(note)}></i>
            <i className="fas fa-search mx-2" onClick={handleCompareClick}></i>
          </div>

          <p className="card-text">Starting: {note.start}</p>
          <p className="card-text">Destination: {note.end}</p>
          <p className="card-text">Bus Name: {note.title}</p>
          <p className="card-text">Bus Number: {note.description}</p>
          <p className="card-text">Tag: {note.tag}</p>

          {showCompareForm && <CompareForm note={note} />}
        </div>
      </div>
    </div>
  );
};

export default Noteitem;
