import React, { useState } from 'react';

const SearchForm = ({ items }) => {
  const [start, setStart] = useState('');
  const [destination, setDestination] = useState('');
  const [filteredItems, setFilteredItems] = useState([]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name === 'start') {
      setStart(value);
    } else if (name === 'destination') {
      setDestination(value);
    }
  };

  const handleSearch = (e) => {
    e.preventDefault(); // Prevent the default form submission

    // Filter items based on start and destination
    const filtered = items.filter((item) => {
      // Check if item is defined and has 'start' and 'destination' properties
      if (item && item.start && item.destination) {
        return (
          item.start.toLowerCase().includes(start.toLowerCase()) &&
          item.destination.toLowerCase().includes(destination.toLowerCase())
        );
      }
      return false;
    });

    setFilteredItems(filtered);
  };

  return (
    <div>
      <h2>Search for Items</h2>
      <form onSubmit={handleSearch}>
        <div>
          <label htmlFor="start">Starting Position:</label>
          <input
            type="text"
            id="start"
            name="start"
            value={start}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label htmlFor="destination">Destination:</label>
          <input
            type="text"
            id="destination"
            name="destination"
            value={destination}
            onChange={handleInputChange}
          />
          <button type="submit">Search</button>
        </div>
      </form>
      {filteredItems.length === 0 ? (
        <p>No matching items found.</p>
      ) : (
        <div>
          <h3>Matching Items:</h3>
          <ul>
            {filteredItems.map((item, index) => ( // Added 'index' as a key
              <li key={index}>
                Starting: {item.start}, Destination: {item.destination}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default SearchForm;
