import React, { useState } from 'react';

const SearchForm = ({ items }) => {
  const [start, setStart] = useState('');
  const [destination, setDestination] = useState('');
  const [filteredItems, setFilteredItems] = useState([]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name === 'start') {
      setStart(value);
    } else if (name === 'destination') {
      setDestination(value);
    }

    // Filter items based on start and destination
    const filtered = items.filter((item) => {
      return (
        item.start.toLowerCase().includes(start.toLowerCase()) &&
        item.destination.toLowerCase().includes(destination.toLowerCase())
      );
    });

    setFilteredItems(filtered);
  };

  return (
    <div>
      <h2>Search for Items</h2>
      <form>
        <div>
          <label htmlFor="start">Starting Position:</label>
          <input
            type="text"
            id="start"
            name="start"
            value={start}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label htmlFor="destination">Destination:</label>
          <input
            type="text"
            id="destination"
            name="destination"
            value={destination}
            onChange={handleInputChange}
          />
        </div>
      </form>
      <h3>Matching Items:</h3>
      <ul>
        {filteredItems.map((item) => (
          <li key={item.id}>
            Starting: {item.start}, Destination: {item.destination}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SearchForm;
